class Todo{constructor(t,o){this.content=t,this.state=o}changeState(){this.state=!this.state}}class TodoManager{constructor(t){this.todolist=[]}addItem(t,o=!1){const s=new Todo(t,o);this.todoList.push(s)}getItems(){return this.todoList}getLeftTodoCount(){return this.todoList.filter(t=>!1===t.state).length}}const myTodo=new TodoManager;console.log(myTodo),myTodo.addItem("할일"),myTodo.getItems();